import React, { useState, useEffect } from 'react'
import { Button, Spinner } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [data, setData] = useState({})


  return (
    <div>
      <p>hier sollte es stehen: {JSON.stringify(data)}</p>
      <GetTestDataButton>Get Test Data</GetTestDataButton>
    </div>
  )

}

export default App

function GetTestDataButton({ children, ...props }) {
  const [isButtonLoading, setIsButtonLoading] = useState(false);

  const handleOnClick = async () => {
    setIsButtonLoading(true);
    await fetch("/getTestData").then(res => res.json()).then(data => { setData(data) }, [])
    setIsButtonLoading(false);
  }

  return (
    <Button variant="dark" onClick={async () => await handleOnClick()} className="button" {...props}>
      {isButtonLoading ? <Spinner /> : children}
    </Button>
  );
}
